import React from "react";
import Flex from "../components/layout/Flex";
import Grid, { GridItem } from "../components/layout/Grid";
import "../styles/contactpage.css";

const ContactPage = () => {
  return (
    <div className="page-padding">
      <Flex stack={true} spacing={48}>
        <Flex stack={true} spacing={16}>
          <h1 className="bordered">Contact Me</h1>
          <p className="bordered">
            Hi there, contact me to ask me about anything you have in mind.
          </p>
        </Flex>

        <form className="bordered">
          <Flex stack={true} spacing={24}></Flex>
          <Grid span={12} gap="20px">
            <GridItem
              span={6}
              md={12}
              className="bordered"
              style={{ width: "100%" }}
            >
              <label>First name</label>
              <input />
            </GridItem>
            <GridItem span={6} md={12}>
              <label>Last name</label>
              <input />
            </GridItem>
          </Grid>
          <div>
            <label>Email</label>
            <input />
          </div>
          <Flex stack={true}>
            <label>Message</label>
            <textarea />
          </Flex>
        </form>
      </Flex>
    </div>
  );
};

export default ContactPage;
