import React from "react";
import Grid, { GridItem } from "./layout/Grid";
import logo from "../assets/logo.svg";
import l4G from "../assets/I4G.svg";
import "../styles/footer.css";
import Flex from "./layout/Flex";

const Footer = () => {
  return (
    <Grid
      className="footer-position footer-padding bordered"
      span={12}
      gap="12px"
    >
      <GridItem span={3} md={12} className="bordered">
        <div>
          <img src={logo} alt="" />
        </div>
      </GridItem>
      <GridItem span={7} md={12} className="bordered">
        <div jc="center" ai="center">
          <p>HNG Internship 9 Frontend Task</p>
        </div>
      </GridItem>
      <GridItem span={2} md={12} className="bordered">
        <div className="logo-position">
          <img src={l4G} alt="" />
        </div>
      </GridItem>
    </Grid>
  );
};

export default Footer;
